--
-- Структура таблицы `comments`
--

CREATE TABLE `comments` (
  `id` int NOT NULL,
  `parent_id` int NOT NULL DEFAULT '0',
  `comment` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `news_id` int NOT NULL,
  `user_id` int NOT NULL,
  `add_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

--
-- Дамп данных таблицы `comments`
--

INSERT INTO `comments` (`id`, `parent_id`, `comment`, `news_id`, `user_id`, `add_date`) VALUES
(1, 0, 'Прекрасная статья, лишние усложнения для понимания — я считаю понты. Еще раз. я считаю. Человек сразу обозначил для кого статья, и может я тупой, но именно это я и искал. Спасибо автору!', 1, 1, '2022-12-05 08:34:25'),
(2, 1, 'На самом деле это не так — как минимум nvarchar и identity будут не во всех СУБД.\r\nВыбран очень странный стиль написания имён объектов — где-то используется схема, где-то не используется. ИМХО, в «справочном» посте стиль должен быть унифицирован.\r\nПлюс, вы много где объявляете поле как nvarchar, а вставляете туда varchar — не надо так)', 1, 2, '2022-12-06 15:38:43'),
(3, 0, 'Там не только «звучит», там коннотация достаточно негативная.\r\nЭто для славян легко написать invalid\\invalide для людей (или как в одной истории про дизайнера — он сокращал button до butt, и в проекте у него были цвета с размерами, так что даже дошло до black_big_butt), но для англоязычных это слова и у них есть свой смысл.', 1, 1, '2022-12-06 08:48:33'),
(4, 0, 'Заявка на создание своего введения в реляционные БД для новичков имеет право на жизнь. Как говорится пока кому-нибудь не объяснишь, сам не поймешь. Написано конечно топорно, но не у каждого найдется сил и на это. Дальше только полировать.\r\nСвязи один-к-одному не такие уж и редкие. На больших объемах данных возникает необходимость в вертикальном секционировании для ускорения работы запросов.\r\nЕсли некий объект имеет множество атрибутов, часть из которых используется очень часто, а часть очень редко, целесообразно хранить их отдельно друг от друга, чтобы уменьшить расходы на чтение диска.\r\n«вторичный ключ» перекликается с «альтернативный ключ» — по этому это название плохо применимо для «foreign key» = «внешний ключ».', 1, 2, '2022-12-06 14:18:21');

